insert into team(id,date_of_foundation,description,img,name,site,discipline_id)
values (7,'2012-05-12','28 июня 2014 года Virtus.pro впервые подписывают состав по Лиге Легенд. Счастливчиками становится команда Dragon, которая лидировала в первых двух регулярных сезонах SLTV StarSeries и занимала высокие места на других онлайн- и оффлайн-турнирах. На LAN-финалах первого сезона «звездной лестницы» команда заняла второе место, уступив лишь украинцам Carpe Diem. ex-Dragon уже точно попадают на LAN-финалы второго сезона, победа на котором принесет им не только очередной титул, но и шанс попасть на чемпионат мира 2014 сезона - победитель SLTV StarSeries Season 2 попадает на WildCard турнир в Кёльне.','https://static.wikia.nocookie.net/lolesports_gamepedia_en/images/9/90/Virtus.prologo_square.png/revision/latest?cb=20170802172032','Virtus.pro','https://virtus.pro/',3);
insert into team(id,date_of_foundation,description,img,name,site,discipline_id)
values (8,'2012-05-12','Одна из самых стабильных и сильных команд в Европе. Первые, кто нарушил беспроирышную серию Gambit Gaming. С 2014 года состав разделился надвое, и команда вместе с тремя игроками переехала в США.','https://evilgeniuses.gg/wp-content/uploads/2020/05/Crest-Dark-1.png','Evil Geniuses','https://evilgeniuses.gg/',3);










